sourcesContent:
import express from 'express';
const router = express.Router();
import fs from 'fs';
import path from 'path';
import multiparty from 'multiparty';

router.get('/', (req, res) => {
    fs.readFile(path.join(__dirname, "todos.json"), 'utf-8', (err, data) => {
        if(err) {
            return res.status(500).json({
                message: "Lấy m thất bại!"
            })
        }
        return  res.status(200).json({
            message: "Lấy todos thành công!",
            data: JSON.parse(data)
        })
    })
})

router.delete('/:todoId', (req, res) => {
    if (req.params.todoId) {
        fs.readFile(path.join(__dirname, "todos.json"), 'utf-8', (err, data) => {
            if(err) {
                return res.status(500).json({
                    message: "Lấy todos thất bại!"
                })
            }
            let todos = JSON.parse(data);
            let todoDelete = todos.find(todo => todo.id == req.params.todoId);
            todos = todos.filter(todo => todo.id != req.params.todoId);

            fs.writeFile(path.join(__dirname, "todos.json"),JSON.stringify(todos), (err) => {
                if(err) {
                    return res.status(500).json({
                        message: "Lưu file thất bại!"
                    })
                }

                fs.unlink(path.join(__dirname, "../../../public/todo-images/" + todoDelete.avatar), (err) => {
                    if(err) {
                        return res.status(500).json({
                            message: "Xóa ảnh thất bại!"
                        })
                    }
                    
                    return res.status(200).json(
                        {
                            message: "Xóa thành công!"
                        }
                    )
                })
                
            })
        })
    }else {
        return res.status(500).json(
            {
                message: "Vui lòng truyền todoId!"
            }
        )
    }
})

router.post('/', (req,res) => {
    //import multiparty from 'multiparty';
    let form = new multiparty.Form();

    form.parse(req, (err, fields, files) => {
        if (err) {
            return res.status(500).send("Lỗi đọc form!")
        }
        console.log("fields", fields)
        console.log("files", files)

        let fileName = "todo_" + Date.now();
        let fileTail = "." + files.avatar[0].originalFilename.split('.')[files.avatar[0].originalFilename.split('.').length - 1];
        let fileNameSave = fileName + fileTail;

        fs.copyFile(files.avatar[0].path, path.join(__dirname, "../../../public/todo-images/" + fileNameSave), (err) => {
            if(err) {
                console.log("err", err)
                return res.status(500).json({
                    message: "Lưu file thất bại!"
                })
            }

            let newtodo = {
                id: Date.now(),
                name: fields.name[0],
                weight: fields.weight[0],
                avatar: fileNameSave
            }

            fs.readFile(path.join(__dirname, "todos.json"), 'utf-8' ,(err, data) => {
                if(err) {
                    return res.status(500).json(
                        {
                            message: "Đọc dữ liệu thất bại!"
                        }
                    )
                }

                let oldData = JSON.parse(data);
                oldData.unshift(newtodo)

                fs.writeFile(path.join(__dirname, "todos.json"), JSON.stringify(oldData), (err) => {
                    if(err) {
                        return res.status(500).json(
                            {
                                message: "Ghi file thất bại!"
                            }
                        )
                    }
                    return res.redirect('/todos')
                })
            })
        })
    })
})

module.exports = router;

